DROP DATABASE IF EXISTS papyrus;CREATE DATABASE papyrus;USE papyrus;CREATE TABLE produit(   codart CHAR(4) ,   libart VARCHAR(30)  NOT NULL,   stkale INT NOT NULL,   stkph INT NOT NULL,   qteann INT NOT NULL,   unimes CHAR(5)  NOT NULL,   PRIMARY KEY(codart));CREATE TABLE fournis(   numfou VARCHAR(25) ,   nomfou VARCHAR(25)  NOT NULL,   ruefou VARCHAR(25)  NOT NULL,   posfou CHAR(5)  NOT NULL,   vilfou VARCHAR(30)  NOT NULL,   confou VARCHAR(15)  NOT NULL,   satisf INT NOT NULL,   PRIMARY KEY(numfou));CREATE TABLE entcom(   numcom INT,   obscom VARCHAR(50) ,   datcom DATE NOT NULL,   numfou VARCHAR(25)  NOT NULL,   PRIMARY KEY(numcom),   UNIQUE(numfou),   FOREIGN KEY(numfou) REFERENCES fournis(numfou));CREATE TABLE ligcom(   numlig INT,   qtecde INT NOT NULL,   priuni VARCHAR(50)  NOT NULL,   qteliv INT NOT NULL,   derlv DATETIME,   numcom INT NOT NULL,   codart CHAR(4)  NOT NULL,   PRIMARY KEY(numlig),   UNIQUE(codart),   FOREIGN KEY(numcom) REFERENCES entcom(numcom),   FOREIGN KEY(codart) REFERENCES produit(codart));CREATE TABLE vente(   codart CHAR(4) ,   numfou VARCHAR(25) ,   delliv INT,   qte1 INT NOT NULL,   prix1 INT NOT NULL,   qte2 INT,   pri2 INT,   qte3 INT,   prix3 VARCHAR(50) ,   PRIMARY KEY(codart, numfou),   FOREIGN KEY(codart) REFERENCES produit(codart),   FOREIGN KEY(numfou) REFERENCES fournis(numfou));-- supprime l'utilisateur quinella pour parrer l'érreur 1396 --DROP USER IF EXISTS "quinella"@"localhost";CREATE USER "quinella"@"localhost" IDENTIFIED BY "mot de passe";-- création de l'utilisateur 2 --DROP USER IF EXISTS"itezed"@"194.153.88.141";CREATE USER "itezed"@"194.153.88.141" IDENTIFIED BY "just mè";-- attribution des prévilièges --GRANT ALL PRIVILEGES ON papyrus.* TO "quinella"@"localhost";GRANT SELECT ON papyrus.* TO "itezed"@"194.153.88.141";flush PRIVILEGES;-- attribution des prévilièges ---- % spécifie n'importe quelle adresse ip--GRANT ALL PRIVILEGES ON pap